// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./MockERC20.sol";

contract SimpleAMM {
    address public tokenA;
    address public tokenB;

    uint public reserveA;
    uint public reserveB;

    uint public totalSupply;
    mapping(address => uint) public balanceOf;

    uint public constant FEE = 3; // 0.3%
    uint public constant FEE_DENOMINATOR = 1000;

    event Swap(
        address indexed sender,
        address indexed tokenIn,
        address indexed tokenOut,
        uint amountIn,
        uint amountOut
    );

    event Mint(address indexed sender, uint amountA, uint amountB, uint liquidity);
    event Burn(address indexed sender, uint amountA, uint amountB);

    constructor(address _tokenA, address _tokenB) {
        tokenA = _tokenA;
        tokenB = _tokenB;
    }

    function _update(uint _reserveA, uint _reserveB) private {
        reserveA = _reserveA;
        reserveB = _reserveB;
    }

    function mint(address to) external returns (uint liquidity) {
        uint balanceA = MockERC20(tokenA).balanceOf(address(this));
        uint balanceB = MockERC20(tokenB).balanceOf(address(this));

        uint amountA = balanceA - reserveA;
        uint amountB = balanceB - reserveB;

        if (totalSupply == 0) {
            liquidity = sqrt(amountA * amountB);
        } else {
            liquidity = min(amountA * totalSupply / reserveA, amountB * totalSupply / reserveB);
        }

        require(liquidity > 0, "Insufficient liquidity minted");

        balanceOf[to] += liquidity;
        totalSupply += liquidity;

        _update(balanceA, balanceB);

        emit Mint(msg.sender, amountA, amountB, liquidity);
    }

    function burn(address to) external returns (uint amountA, uint amountB) {
        uint liquidity = balanceOf[address(this)];

        uint balanceA = MockERC20(tokenA).balanceOf(address(this));
        uint balanceB = MockERC20(tokenB).balanceOf(address(this));

        amountA = liquidity * balanceA / totalSupply;
        amountB = liquidity * balanceB / totalSupply;

        require(amountA > 0 && amountB > 0, "Insufficient liquidity burned");

        balanceOf[address(this)] -= liquidity;
        totalSupply -= liquidity;

        MockERC20(tokenA).transfer(to, amountA);
        MockERC20(tokenB).transfer(to, amountB);

        balanceA = MockERC20(tokenA).balanceOf(address(this));
        balanceB = MockERC20(tokenB).balanceOf(address(this));

        _update(balanceA, balanceB);

        emit Burn(msg.sender, amountA, amountB);
    }

    function swap(uint amountIn, address tokenIn, address tokenOut, uint amountOutMin, address to) external returns (uint amountOut) {
        require(tokenIn == tokenA || tokenIn == tokenB, "Invalid tokenIn");
        require(tokenOut == tokenA || tokenOut == tokenB, "Invalid tokenOut");
        require(tokenIn != tokenOut, "Identical tokens");

        uint balanceA = MockERC20(tokenA).balanceOf(address(this));
        uint balanceB = MockERC20(tokenB).balanceOf(address(this));

        uint amountInWithFee = amountIn * (FEE_DENOMINATOR - FEE);

        if (tokenIn == tokenA) {
            amountOut = (amountInWithFee * balanceB) / (balanceA * FEE_DENOMINATOR + amountInWithFee);
            require(amountOut >= amountOutMin, "Insufficient amountOut");
            MockERC20(tokenB).transfer(to, amountOut);
        } else {
            amountOut = (amountInWithFee * balanceA) / (balanceB * FEE_DENOMINATOR + amountInWithFee);
            require(amountOut >= amountOutMin, "Insufficient amountOut");
            MockERC20(tokenA).transfer(to, amountOut);
        }

        balanceA = MockERC20(tokenA).balanceOf(address(this));
        balanceB = MockERC20(tokenB).balanceOf(address(this));

        _update(balanceA, balanceB);

        emit Swap(msg.sender, tokenIn, tokenOut, amountIn, amountOut);
    }

    function getAmountOut(uint amountIn, address tokenIn, address tokenOut) external view returns (uint amountOut) {
        require(tokenIn == tokenA || tokenIn == tokenB, "Invalid tokenIn");
        require(tokenOut == tokenA || tokenOut == tokenB, "Invalid tokenOut");
        require(tokenIn != tokenOut, "Identical tokens");

        uint balanceA = MockERC20(tokenA).balanceOf(address(this));
        uint balanceB = MockERC20(tokenB).balanceOf(address(this));

        uint amountInWithFee = amountIn * (FEE_DENOMINATOR - FEE);

        if (tokenIn == tokenA) {
            amountOut = (amountInWithFee * balanceB) / (balanceA * FEE_DENOMINATOR + amountInWithFee);
        } else {
            amountOut = (amountInWithFee * balanceA) / (balanceB * FEE_DENOMINATOR + amountInWithFee);
        }
    }

    function sqrt(uint x) private pure returns (uint y) {
        if (x == 0) return 0;
        y = x / 2 + 1;
        while (y > x / y) {
            y = (x / y + y) / 2;
        }
    }

    function min(uint x, uint y) private pure returns (uint) {
        return x < y ? x : y;
    }
}

